byingredient.map:
function(receta) {
  if(receta.etiquetas){
 for(ingrediente in receta.etiquetas) { emit(receta.etiquetas[ingrediente], {_id:receta._id, nombre: receta.nombre, ingredientes: receta.ingredientes, porciones: receta.porciones, tiempo: receta.tiempo, dificultad: receta.dificultad, preparacion: receta.preparacion, etiquetas: receta.etiquetas, cantidadIngredientes: receta.etiquetas.length});}
 }
}
